#import "CustomMenuView.h"
#import <objc/runtime.h>

@implementation CustomMenuWindow

- (instancetype)initWithFrame:(CGRect)frame {
    self = [super initWithFrame:frame];
    if (self) {
        self.windowLevel = UIWindowLevelAlert + 99;
        _menuView = [[CustomMenuView alloc] init];
        _menuView.center = self.center;
        [self addSubview:_menuView];
        self.hidden = NO;
    }
    return self;
}

- (UIView *)hitTest:(CGPoint)point withEvent:(UIEvent *)event {
    CGPoint pointInMenuView = [self convertPoint:point toView:self.menuView];
    return [self.menuView pointInside:pointInMenuView withEvent:event] ? [super hitTest:point withEvent:event] : nil;
}

@end

@implementation CustomMenuView {
    NSMutableSet *_expandedSections;  
}

#pragma mark - 初始化与配置

- (instancetype)init {
    self = [super init];
    if (self) {
        _expandedSections = [NSMutableSet set];
        [self setupNotifications];
        [self initializeMenuData];
        [self configureUI];
    }
    return self;
}

- (void)setupNotifications {
    [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(handleUpdateTableView) name:@"UpdateTableViewNotification" object:nil];
    [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(handleUpdateTableViews) name:@"UpdateTableViewNotifications" object:nil];
}

